

function MyTogglePanel() {
    var x = document.getElementById("MyPanel");
    var btn = document.getElementById("btnToggle");

    if (x.style.display === "none") {
        x.style.display = "block";
        btn.getElementsByTagName("span")[0].setAttribute("class", "glyphicon  glyphicon-minus");

    } else {
        x.style.display = "none";

        btn.getElementsByTagName("span")[0].setAttribute("class", "glyphicon  glyphicon-plus");
    }
}

function MyToggleSearch() {
	var x = document.getElementById("AdSearch");
	var btn = document.getElementById("btnSearchToggle");

	if (x.style.display === "none") {
		x.style.display = "block";

	} else {
		x.style.display = "none";
	}
}









$(document).ready(function () {
    //$("#sidebar").mCustomScrollbar({
    //    theme: "minimal"
    //});

    $('#dismiss, .overlay').on('click', function () {
        $('#sidebar').removeClass('active');
        $('.overlay').removeClass('active');
    });

    $('#sidebarCollapse').on('click', function () {
        $('#sidebar').addClass('active');
        $('.overlay').addClass('active');
        $('.collapse.in').toggleClass('in');
        $('a[aria-expanded=true]').attr('aria-expanded', 'false');
    });
});


